    // Cuando el componente se desmonta
    return () => {
      alert("Componente desmontado!!")
    };

  },[]); // Se ejecuta una vez porque le paso el array vacio

  return (
    <div>
      <hr />
      <h3>Saludos Alejandro ¿Qué tal estas?</h3>
      <botton onClick={e => {
        alert("Bienvenido!!")
      }}>Mostrar alerta</botton>
    </div>
  )
}
